"use strict";

var TODO_COMPONENT = "\nclass TodoList\n\n  include React::Component\n\n  required_param :items, type: [String]\n\n  def render\n    ul do\n      items.each_with_index do |item, index|\n        li(key: \"item - #{index}\") { item }\n      end\n    end\n  end\nend\n\nclass TodoApp\n\n  include React::Component\n\n  define_state items: [], text: \"\"\n\n  def render\n    div do\n      h3 { \"TODO\" }\n      TodoList items: items\n      div do\n        input(value: text).on(:change) do |e|\n          text! e.target.value\n        end\n        button { \"Add ##{items.length+1}\" }.on(:click) do |e|\n          items! (items + [text!(\"\")])\n        end\n      end\n    end\n  end\nend\n\nReact.render(React.create_element(TodoApp), Element[\"#todo-target\"])\n";

React.render(React.createElement(ReactPlayground, { codeText: TODO_COMPONENT, elementId: "todo-target" }), document.getElementById('todoExample'));